        public void Main()
        {
            int floor = 0;
            int pos = 1;
            foreach (var dir in input)
            {
                if (dir == '(')
                    floor++;
                else if (dir == ')')
                    floor--;
                else
                    throw new Exception("Unkown direction");

                if (floor == -1)
                    Console.WriteLine("Entered basement on pos " + pos);

                pos++;
            }

            Console.WriteLine(floor);
        }

